import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { NavLink, useParams } from 'react-router-dom';
import Navbar from '../partials/Navbar';


const MentorDetails = () => {
    const [mentor, setProduct] = useState(null);
    const [quantity, setQuantity] = useState(1);
    const {id} = useParams();
    useEffect(() => {
        // Fetch mentor data from backend
        axios.get(`http://localhost:8000/users/${id}`)
            .then(response => {
                setProduct(response.data.user);
            })
            .catch(error => {
                console.error('Error fetching mentor data:', error);
            });
    }, [setProduct]);

    const handleAddToCart = () => {
        axios.post(`http://localhost:8000/user/${mentor._id}/add`, { quantity })
            .then(response => {
                alert('Product added to cart');
            })
            .catch(error => {
                console.error('Error adding mentor to cart:', error);
            });
    };

    const handleAddToWishlist = () => {
        axios.post(`http://localhost:8000/user/${mentor._id}/addwishlist`)
            .then(response => {
                alert('Product added to wishlist');
            })
            .catch(error => {
                console.error('Error adding mentor to wishlist:', error);
            });
    };


    if (!mentor) {
        return <div>Loading...</div>;
    }

    return (
        <div className="py-3 py-md-5 bg-light">
            <Navbar />
            <div className="container">
                <div className="row">
                    <div className="col-md-5 mt-3">
                        <div className="bg-white border">
                            <img src="data:image/png;base64," className="w-100" alt="Product" />
                        </div>
                    </div>
                    <div className="col-md-7 mt-3">
                        <div className="product-view">
                            <h4 className="product-name">
                                {mentor.name}
                                {/* <label className="label-stock bg-success">In Stock</label> */}
                            </h4>
                            <hr />
                            <div>
                                <span className="selling-price">Roles:-{mentor.roles.join()}</span>
                            </div>
                           
                            <div className="mt-2">
                                <button onClick={handleAddToCart} className="btn btn1">
                                    <i className="fa fa-shopping-cart"></i> Add To Cart
                                </button>
                                <button onClick={handleAddToWishlist} className="btn btn1">
                                    <i className="fa fa-heart"></i> Add To Wishlist
                                </button>
                               
                            </div>
                            <a href='/home/mentor/appointment'>
                            <button type="button" class="btn btn-success">Book Appointment</button></a>
                            <div className="mt-3">
                                <h5 className="mb-0">Small Description</h5>
                                <p>{mentor.description}</p>
                            </div>
                        </div>  
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-12 mt-3">
                        <div className="card">
                            <div className="card-header bg-white">
                                <h4>Description</h4>
                            </div>
                            <div className="card-body">
                                <p>
                                    Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.
                                </p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default MentorDetails ;
